import React, { useState } from 'react';
import { ThemeProvider } from './context/ThemeContext';
import AccountListPage from "./modules/accounts/pages/AccountListPage";


const App: React.FC = () => {
    const [activeModule, setActiveModule] = useState(false);

    return (
        <ThemeProvider>
            <div>
                {!activeModule && (
                    <div style={{ padding: 20 }}>
                        <h1>üè¶ PocketBank - Mod√ºl Se√ßici</h1>
                        <button
                            onClick={() => setActiveModule(true)}
                            style={{
                                padding: '10px 18px',
                                backgroundColor: '#6a4ab1',
                                color: '#fff',
                                fontSize: '1rem',
                                border: 'none',
                                borderRadius: '8px',
                                cursor: 'pointer',
                                marginBottom: '24px',
                                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                                transition: 'background-color 0.3s ease'
                            }}
                            onMouseOver={(e) => (e.currentTarget.style.backgroundColor = '#573b97')}
                            onMouseOut={(e) => (e.currentTarget.style.backgroundColor = '#6a4ab1')}
                        >
                            üöÄ Mod√ºl 2 - Account Management
                        </button>
                    </div>
                )}

                {activeModule && <AccountListPage />}
            </div>
        </ThemeProvider>
    );
};

export default App;
